package control_statement;

public class Else_ifladder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int mark=75;
		if(mark>=90) {
			System.out.println("A+");
		}
		else if(mark>=80) {
			System.out.println("A");
		}
		else if(mark>=70) {
			System.out.println("B+");
		}
		else if(mark>=60) {
			System.out.println("B");
		}
		else if(mark>=60) {
			System.out.println("C");
		}
	}

}
